import {View, Text, ScrollView, TextInput, Button} from 'react-native';
import React, { useState, useEffect } from 'react';
import Row from '../components/Row';
import Player from './Player';
import PlayerMinimized from './playerMinimized';
import SpotifySearch from '../data/spotifySearch'

const Search = ({ navigation }) => {
    const [query, setQuery] = useState('');
    console.log(query)
    let results = SpotifySearch(query);
    // // console.log(results)
    var data = results
    // console.log(data)
    return (
        <View style={{paddingTop: 50}}>
            <TextInput onChangeText={(text) => setQuery(text)} />
            <Button title="Buscar" onPress={async () => {data = await SpotifySearch(query)
                console.log(data.tracks)}} />
            <ScrollView overScrollMode="never">
                <Text>Resultados</Text>
                <Row navigation={navigation} tituloSeccion="Canciones" data={data.tracks} />
            </ScrollView>
        </View>
    );
};

export default Search;
