import {React, useEffect, useState} from 'react'
import Constants from 'expo-constants'
import Item from './Item'
import ItemHistorial from './ItemHistorial'
import { StyleSheet, Text, View, Alert, TouchableNativeFeedback, FlatList, TouchableNativeFeedbackBase } from 'react-native'


let id = 1
const listHistorial = ({ navigation}) => {
const [placeholderData, setPlaceholderData] = useState({});

useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`http://192.168.100.57:3001/api/historial/${id}`, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
            },
        })

        if (!response.ok) {
          throw new Error('La solicitud no fue exitosaa');
        }
        const data = await response.json();
        setPlaceholderData(data)
        data.forEach(async element => {
            try {
                const response = await fetch(`http://192.168.100.57:3001/api/canciones/${element.nombre}`, {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                })

                if (!response.ok) {
                throw new Error('La solicitud no fue exitosaa');
                }
                const data = await response.json();
                data
                } catch (error) {
                console.error('Error en la solicitud:', error);
                // setUsuarios([{ nombre: 'Error', contraseña: 'Error', email: 'Error' }]);
      }
        });
        // setUsuarios(data);
        console.log(data);
      } catch (error) {
        console.error('Error en la solicitud:', error);
        // setUsuarios([{ nombre: 'Error', contraseña: 'Error', email: 'Error' }]);
      }
    };

    fetchData(); // Llama a la función asincrónica para obtener los datos
  }, []);
    console.log(placeholderData); 
    // Ahora también puedes usar navigation.navigate, navigation.push, etc.
    return(
        <View overScrollMode='never'>            
            <FlatList overScrollMode='never'
                // data={data && data.data.tracks}
                data={placeholderData}
                horizontal={true}
                renderItem={({ item }) => (
                    <View>
                        <ItemHistorial key={item} img={``} nombreItem={item.nombre} descItem={item.tipo} />                        
                    </View>
                )}
            />    
        </View>  
    )
    
}

export default listHistorial

const listStyle = StyleSheet.create({    
    titleSection:{
        fontSize: 22,
        backgroundColor: 'black',
        color: 'white',        
        paddingLeft: 10,
        borderRadius: 3,
        textTransform: 'capitalize' 
    }
})
