//         method: "POST",
//         headers: {
//             'Content-Type': 'application/json',
//             body: JSON.stringify(datos),
//         },
//     })
//     .then(response => {
//         if (response.ok) {
//             return response.json(); // Obtener el texto de la respuesta
//         } else {
//             throw new Error('La solicitud no fue exitosa');
//         }
//     })
//     .then(result => {
//         console.log('Respuesta del servidor:', result);
        
//     })
//     .catch(error => {
//         console.error('Error en la solicitud:', error);
//     });
// }

    return (
        <>
        <ScrollView 
        centerContent
        > 
            <View>
                <KeyboardAwareScrollView>
                <Text>Formulario</Text>
                {/* <Avatar 
                    rounded
                    size="large"
                    onPress={SubirImagen}
                    source={
                        user.photoURL ? {url: photoURL} : require("a")
                    }
                /> */}
                {/* <Avatar 
                placeholder="imagen"
                onPress={() => SubirImagen()}
                source={require("./default.webp")}
                /> */}
                <TextInput
                    placeholder="Nombre"
                    value={nombre}
                    onChangeText={text => setNombre(text)}
                    returnKeyType="next"
                    onSubmitEditing={() => secondTextInputRef.current.focus()}
                    blurOnSubmit={false}
                />
                <TextInput
                    placeholder="correo"
                    // autoCapitalize='none'
                    ref={secondTextInputRef}
                    keyboardType='email-address'
                    value={correo}
                    onChangeText={text => setCorreo(text)}
                    returnKeyType="next"
                    onSubmitEditing={() => thirdTextInputRef.current.focus()}
                    blurOnSubmit={false}
                />
                <TextInput
                    placeholder="contraseÃ±a"
                    value={contrasenna}
                    ref={thirdTextInputRef}
                    onChangeText={text => setContrasenna(text)}
                    secureTextEntry={true}
                    returnKeyType="next"
                    onSubmitEditing={() => Datos()}
                    blurOnSubmit={false}
                />
                {/* <Button title="Verificar POST" onPress={EnviarPost} /> */}
                <Button title="Iniciar" onPress={Datos}/>
                <Button title="Registrarse" onPress={()=>navigation.navigate('Register')}/>
                </KeyboardAwareScrollView>
            </View>
            </ScrollView>
        </>
    )
}
export default Login
