import {View, Text, ScrollView, TextInput, Button} from 'react-native';
import {React, useState } from 'react';
import Row from '../components/Row';
import Player from './Player';
import PlayerMinimized from './playerMinimized';
import SpotifySearch from '../data/spotifyApi'

const Search = ({ navigation }) => {
    const [data, setData] = useState(null);
    const [query, setQuery] = useState('');
    function handleSearch() {
        const results = SpotifySearch(query);
        setData(results);
    };
    return (
        <View>
            <TextInput onChangeText={(text) => setQuery(text)} />
            <Button title="Buscar" onPress={handleSearch()} />
            <ScrollView overScrollMode="never">
                <Text>Resultados</Text>
                <Row navigation={navigation} tituloSeccion="Canciones" data={data} />
            </ScrollView>
        </View>
    );
};

export default Search;
